package ulissesmb.com.avaliacaoPleno.domain.modelo;import org.hibernate.annotations.NaturalId;import ulissesmb.com.avaliacaoPleno.domain.enuns.RoleEnum;import javax.persistence.*;@Entity@Table(name = "TP_ROLES")public class Role {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @Enumerated(EnumType.STRING)    @NaturalId    @Column(length = 60)    private RoleEnum name;    public Role() {    }    public Role(RoleEnum name) {        this.name = name;    }    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public RoleEnum getName() {        return name;    }    public void setName(RoleEnum name) {        this.name = name;    }}