package ulissesmb.com.avaliacaoPleno.domain.modelo;import com.fasterxml.jackson.annotation.JsonIgnore;import org.dom4j.tree.AbstractEntity;import javax.persistence.*;import javax.validation.constraints.NotEmpty;import java.io.Serializable;import java.util.Objects;@Entity@Table(name = "TP_USUARIO")@SequenceGenerator(name = "SEQ_USUARIO", sequenceName = "SEQ_USUARIO", initialValue = 1, allocationSize = 1)public class Usuario extends AbstractEntity implements Serializable {    @Id    @GeneratedValue(strategy = GenerationType.SEQUENCE, generator = "SEQ_USUARIO")    private Long id;    @NotEmpty    @Column(unique = true)    private String login;    @NotEmpty    @JsonIgnore    private String senha;    @NotEmpty    private String nome;    @NotEmpty    private Boolean admin;    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public String getLogin() {        return login;    }    public void setLogin(String login) {        this.login = login;    }    public String getSenha() {        return senha;    }    public void setSenha(String senha) {        this.senha = senha;    }    public String getNome() {        return nome;    }    public void setNome(String nome) {        this.nome = nome;    }    public Boolean getAdmin() {        return admin;    }    public void setAdmin(Boolean admin) {        this.admin = admin;    }    @Override    public boolean equals(Object o) {        if (this == o) return true;        if (o == null || getClass() != o.getClass()) return false;        Usuario usuario = (Usuario) o;        return id.equals(usuario.id) &&                login.equals(usuario.login);    }    @Override    public int hashCode() {        return Objects.hash(id, login);    }}