package ulissesmb.com.avaliacaoPleno.domain.modelo;import com.fasterxml.jackson.annotation.JsonIgnore;import org.dom4j.tree.AbstractEntity;import ulissesmb.com.avaliacaoPleno.domain.modelo.audit.DateAudit;import javax.persistence.*;import javax.validation.constraints.NotEmpty;import java.io.Serializable;import java.util.HashSet;import java.util.Objects;import java.util.Set;@Entity@Table(name = "TP_USUARIO")public class Usuario extends DateAudit implements Serializable {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @NotEmpty    @Column(unique = true)    private String login;    @NotEmpty    @JsonIgnore    private String senha;    @NotEmpty    private String nome;    @NotEmpty    private Boolean admin;    @ManyToMany(fetch = FetchType.EAGER, cascade = CascadeType.ALL)    @JoinTable(name = "TP_USUARIO_ROLES",            joinColumns = @JoinColumn(name = "USUARIO_ID"),            inverseJoinColumns = @JoinColumn(name = "ROLE_ID"))    private Set<Role> roles = new HashSet<>();    public Usuario() {    }    public Usuario(@NotEmpty String login, @NotEmpty String senha, @NotEmpty String nome, @NotEmpty Boolean admin, Set<Role> roles) {        this.login = login;        this.senha = senha;        this.nome = nome;        this.admin = admin;        this.roles = roles;    }    public Usuario(@NotEmpty String login, @NotEmpty String senha, @NotEmpty String nome, @NotEmpty Boolean admin) {        this.login = login;        this.senha = senha;        this.nome = nome;        this.admin = admin;    }    public Usuario(@NotEmpty String login, @NotEmpty String senha, @NotEmpty String nome) {        this.login = login;        this.senha = senha;        this.nome = nome;    }    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public String getLogin() {        return login;    }    public void setLogin(String login) {        this.login = login;    }    public String getSenha() {        return senha;    }    public void setSenha(String senha) {        this.senha = senha;    }    public String getNome() {        return nome;    }    public void setNome(String nome) {        this.nome = nome;    }    public Boolean getAdmin() {        return admin;    }    public void setAdmin(Boolean admin) {        this.admin = admin;    }    public Set<Role> getRoles() {        return roles;    }    public void setRoles(Set<Role> roles) {        this.roles = roles;    }    @Override    public boolean equals(Object o) {        if (this == o) return true;        if (o == null || getClass() != o.getClass()) return false;        Usuario usuario = (Usuario) o;        return id.equals(usuario.id) &&                login.equals(usuario.login);    }    @Override    public int hashCode() {        return Objects.hash(id, login);    }}