package ulissesmb.com.avaliacaoPleno.reposity;import org.springframework.data.repository.CrudRepository;import org.springframework.stereotype.Repository;import ulissesmb.com.avaliacaoPleno.domain.modelo.Cliente;import ulissesmb.com.avaliacaoPleno.domain.modelo.FoneContato;import java.util.List;@Repositorypublic interface IClienteRepository extends CrudRepository<Cliente, Long> {    List<Cliente> findByNome(String nome);    List<FoneContato> findByContatos(Cliente cliente);}