package ulissesmb.com.avaliacaoPleno.service;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import ulissesmb.com.avaliacaoPleno.domain.modelo.Cliente;import ulissesmb.com.avaliacaoPleno.domain.modelo.FoneContato;import ulissesmb.com.avaliacaoPleno.reposity.IClienteRepository;import java.util.ArrayList;import java.util.List;import java.util.Optional;@Servicepublic class ClienteService {    @Autowired    private IClienteRepository clienteRepository;    public List<Cliente> listarTodos() {        List<Cliente> clientes = new ArrayList<>();        clienteRepository.findAll().forEach(clientes::add);        return clientes;    }    public Cliente save(Cliente cliente) {        return clienteRepository.save(cliente);    }    public Optional<Cliente> buscarPorId(Long id) {        return clienteRepository.findById(id);    }    public void delete(Long id) {        clienteRepository.deleteById(id);    }    public List<FoneContato> buscarContatosPorCliente(Long id){        Cliente cliente = buscarPorId(id).get();        List<FoneContato> byContatos = clienteRepository.findByContatos(cliente);        if(byContatos.size() > 0){            return byContatos;        }        return null;    }}