package ulissesmb.com.avaliacaoPleno.service;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import ulissesmb.com.avaliacaoPleno.domain.modelo.EmailContato;import ulissesmb.com.avaliacaoPleno.reposity.IEmailRepository;import java.util.ArrayList;import java.util.List;import java.util.Optional;@Servicepublic class EmailService {    @Autowired    private IEmailRepository emailRepository;    public List<EmailContato> listarTodos() {        List<EmailContato> emails = new ArrayList<>();        emailRepository.findAll().forEach( email ->{            emails.add(email);        });        return emails;    }    public EmailContato save(EmailContato object) {        return emailRepository.save(object);    }    public Optional<EmailContato> buscarPorId(Long id) {        return emailRepository.findById(id);    }    public void delete(Long id) {        emailRepository.deleteById(id);    }}