CREATE TABLE `TP_USUARIO` (  `id` bigint(20) NOT NULL AUTO_INCREMENT,  `nome` varchar(40) NOT NULL,  `login` varchar(15) NOT NULL,  `senha` varchar(150) NOT NULL,  `created_at` timestamp DEFAULT CURRENT_TIMESTAMP,  `updated_at` timestamp DEFAULT CURRENT_TIMESTAMP,  PRIMARY KEY (`id`),  UNIQUE KEY `uk_usuario_login` (`login`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;CREATE TABLE `TP_ROLES` (  `id` bigint(20) NOT NULL AUTO_INCREMENT,  `name` varchar(60) NOT NULL,  PRIMARY KEY (`id`),  UNIQUE KEY `uk_roles_name` (`name`)) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;CREATE TABLE `TP_USUARIO_ROLES` (  `usuario_id` bigint(20) NOT NULL,  `role_id` bigint(20) NOT NULL,  PRIMARY KEY (`usuario_id`,`role_id`),  KEY `fk_user_roles_role_id` (`role_id`),  CONSTRAINT `fk_user_roles_role_id` FOREIGN KEY (`role_id`) REFERENCES `TP_ROLES` (`id`),  CONSTRAINT `fk_user_roles_user_id` FOREIGN KEY (`usuario_id`) REFERENCES `TP_USUARIO` (`id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;